import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import ReactDOM from 'react-dom'
import ModalVideo from 'react-modal-video'
import '../node_modules/react-modal-video/scss/modal-video.scss';


 export class Modal extends Component {

  constructor () {
    super()
    this.state = {
      isOpen: true
    }
    this.openModal = this.openModal.bind(this)
  }

  openModal () {
    this.setState({isOpen: true})
  }

  render () {
    return (
//      startmodal() =
      <div>
        <ModalVideo channel='youtube' isOpen={this.state.isOpen} videoId='L61p2uyiMSo' onClose={() => this.setState({isOpen: false})} />
        {/* <button onClick={this.openModal}>Open</button> */}
      </div>
    )
  }
}

export class MapContainer extends Component {

  state = {
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {},
  };

  onMarkerClick = (props, marker, e) => //Modal.openModal;
  //   this.setState({
  //     selectedPlace: props,
  //     activeMarker: marker,
  //     showingInfoWindow: true
  // });


  onMapClicked = (props) =>
  {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      })
    }
  };
  //<Route path='/faq' component={() => window.location = 'https://example.com/faq.html'}/>

  render() {
    return (
      <div>
      <Map
        google={this.props.google}
        zoom={8}
        onClick={this.onMapClicked}
        initialCenter={{
          lat: 35.787743,
          lng: -78.64425
        }}
        >
        <Marker onClick={this.onMarkerClick}
                name={'School'}
                position={{lat: 35.787743, lng: -78.644257}}
         />
         <Marker onClick={this.onMarkerClick}
                 name={'Snow Day - 1/18/18 <a href="google.com"> FAQ </a>'}
                 position={{lat: 35.7688, lng: -78.6619}}
          />

         <InfoWindow
           marker={this.state.activeMarker}
           visible={this.state.showingInfoWindow}>
             <div>
               <h1>{this.state.selectedPlace.name}</h1>
             </div>
         </InfoWindow>

      </Map>
  //    <Modal/>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyCnY73kvyiQLtQwpli9k2GgD-G0dlAe8N0'
})(MapContainer)

